css:
 .no-border-table {
        font-size: 12px;
        border: none;
        padding: 5px;
        width:110%;
        text-align: right;
}
.small-font-table {
    font-size: 12px;
    width:100%;
    text-align: right;
}
@page {
    size: A4;
    margin: 10mm;
}
.po-table {
    font-size: 12px;
    border: none;
    padding: 5px;
    width:100%;
    text-align: right;
    background-color: white;
    background:white;
}
  #footer {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
  }

html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head" lang="ar" dir="rtl">{{ letter_head }}</div>
    {% endif %}
    {% if letter_footer and not no_letterfooter %}
    <div class="letter-head" lang="ar" dir="rtl">{{ letter_footer }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center" lang="ar" dir="rtl">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center" lang="ar" dir="rtl">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right" lang="ar" dir="rtl">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
<div class="print-heading" lang="ar" dir="rtl">	
    <h2><div>فواتير المبيعات</div>
    <br>
    <small class="sub-heading">{{ doc.name }}</small>				
    </h2>
</div>
<table class="no-border-table" lang="ar" dir="rtl"> 
    <tbody> 
        <tr> 
            <td> <b>اسم العميل</b> <br> {{ doc.customer or "" }} </td>
            <td> <b>التاريخ</b> <br> {{ doc.posting_date or "" }}</td>
            {% if doc.company_tax_id %} 
            <td> <b>الرقم الضريبي للشركة</b> <br> {{ doc.company_tax_id or ""}}</td>
            {% endif %} 
        </tr> 
        <tr> 
            <td></td> 
            <td> <b>تاريخ استحقاق السداد</b><br> {{ doc.due_date or "" }}</td> 
            {% if doc.tax_id %} 
            <td> <b>الرقم الضريبي للعميل</b> <br> {{ doc.tax_id or "" }}</td> 
            {% else %} 
            <td></td> 
            {% endif %} 
        </tr> 
        <tr> 
            {% if doc.status %} <td> <b>الحالة</b> <br> {{ doc.status or "" }}</td> {% endif %} 
        </tr>
    </tbody>
</table>
   {% if doc.po_no or doc.po_date %}
    <div>
        <table style="width: 100%; background-color: white; border-collapse: collapse;" lang="ar" dir="rtl">
            <tr style="width: 100%; background-color: white;">
                <th style="width: 50%; text-align: right; font-size: 12px; padding: 8px background-color: white;">
                    {% if doc.po_no %}
                        تفاصيل امر الشراء&nbsp;&nbsp;{{ doc.po_no }}
                    {% endif %}
                </th>
                <th style="width: 50%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
                    {% if doc.po_date %}
                        تاريخ امر الشراء&nbsp;&nbsp;{{ doc.po_date }}
                    {% endif %}
                </th>
            </tr>
        </table>
    </div>
{% endif %}

    <br>
    
    <div>
        <p style="text-align: right; font-size: 14px;" lang="ar" dir="rtl"><b> الاصناف </b> <br></p>
        <br>
        {% set bouns = doc.items|selectattr('custom_bonus_qty')|list|length > 0 %}
        <table class="small-font-table" lang="ar" dir="rtl">
            <tr style="background-color: #f0f0f0; text-align: right;">
                <th style = "width:7%; text-align: right;">ر.ت</th>
                <th style="text-align: right;">رقم الصنف</th>
                <th style="text-align: right;">اسم الصنف</th>
                <th style="text-align: right;">كمية</th>
                {% if bouns %}
                <th style="text-align: right;">علاوة</th>
                {% endif %}
                <th style="text-align: right;">سعر الصنف</th>
                <th style="text-align: right;">المجموع</th>
            </tr>
            {% for item in doc.items %}
            <tr>
                <td style="text-align: right;">{{ item.idx }}</td>
                <td style="text-align: right;">{{ item.item_code }}</td>
                <td style="text-align: right;">{{ item.item_name }}</td>
                <td style="text-align: right;">{{ item.qty }}</td>
                {% if bouns %}
                <td style="text-align: right;">{{ item.custom_bonus_qty or ''}}</td>
                {% endif %}
                <td style="text-align: right;">{{ item.rate }} د.أ</td>
                <td style="text-align: right;">{{ item.amount }} د.أ</td>
            </tr>
            {% endfor %}
        </table>
    </div>
    <br><br>
    <div>
  <table class="no-border-table">
    <tr>
      <td style="width: 60%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
        <b>الكمية الإجمالية:</b>  &nbsp; &nbsp; &nbsp
        {% set bonus_qty = doc.custom_total_qty_with_bonus if doc.custom_total_qty_with_bonus != 0 else doc.total_qty %}
        {{ bonus_qty }}
      </td>
      <td style="width: 40%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
        <b>الإجمالي غير شامل الضريبة:</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.total }}
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
        <b>مجموع الضرائب والرسوم:</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.total_taxes_and_charges }}
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
        <b>المجموع الإجمالي:</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.grand_total }}
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
    <b>بالكلمات:</b>  &nbsp; &nbsp; &nbsp; 
    {{ frappe.utils.in_words(doc.grand_total) }} دينار أردني
    {% set points = doc.grand_total % (doc.grand_total|int) %}
    {% if points*100 > 1 %}
    {{ frappe.utils.in_words(points*100) }}  قرشا
    {% endif %}
</td>
    </tr>
  </table>
</div>
<br><br>
{% if doc.customer_address or doc.address_display or doc.contact_display or doc.contact_mobile %}
    <div style="width: 100%;">
        <p style="text-align: right; font-size: 14px;" lang="ar" dir="rtl"><b> عنوان الفاتورة </b> <br></p>
        <table style="width: 100%;">
            <tr>
                {% if doc.customer_address %}
                <td style="width: 50%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
                    <b>العنوان</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.customer_address or ''}} 
                </td>
                {% endif %}
                {% if doc.address_display %}
                <td style="width: 50%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
                    <b>العنوان التفصيلي </b> &nbsp;&nbsp;&nbsp;
                    {{ doc.address_display or '' }} 
                {% endif %}
                </td>
            </tr>
            <tr>
                {% if doc.contact_display %}
                <td style="width: 50%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
                    <b>معلومات الاتصال</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.contact_display  or '' }} 
                
                </td>
                {% endif %}
                {% if doc.contact_mobile %}
                <td style="width: 50%; text-align: right; font-size: 12px; padding: 8px; background-color: white;">
                    <b>رقم الهاتف</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.contact_mobile  or ''}} 
                </td>
                {% endif %}
            </tr>
        </table>
    </div>
{% endif %}


<div>
    <p>
        {{ doc.terms }}
    </p>
</div>





<!-- page footer -->
<table id="footer" width="100%">
  <tr>
    <td width="100%">
      {{ footer }}
    </td>
  </tr>
</table>
