css:
 .no-border-table {
        font-size: 12px;
        border: none;
        padding: 5px;
        width:110%;
        text-align: left;
}
.small-font-table {
    font-size: 12px;
    width:100%;
    text-align: left;
}
@page {
    size: A4;
    margin: 10mm;
}
.po-table {
    font-size: 12px;
    border: none;
    padding: 5px;
    width:100%;
    text-align: left;
    background-color: white;
    background:white;
}
  #footer {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
  }

html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head" lang="ar" dir="rtl">{{ letter_head }}</div>
    {% endif %}
    {% if letter_footer and not no_letterfooter %}
    <div class="letter-head" lang="ar" dir="rtl">{{ letter_footer }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center" lang="ar" dir="rtl">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center" lang="ar" dir="rtl">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right" lang="ar" dir="rtl">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}
{{ add_header(0,1,doc,letter_head, no_letterhead) }}
<div class="print-heading" lang="en" style="text-align: left;">	
    <h2><div>Sales Invoice</div>
    <br>
    <small class="sub-heading">{{ doc.name }}</small>				
    </h2>
</div>
<table class="no-border-table"> 
    <tbody> 
        <tr> 
            <td> <b>Customer:</b> <br> {{ doc.customer or "" }} </td>
            <td> <b>Date:</b> <br> {{ doc.posting_date or "" }}</td>
            {% if doc.company_tax_id %} 
            <td> <b>Compant Tax ID:</b> <br> {{ doc.company_tax_id or ""}}</td>
            {% endif %} 
        </tr> 
        <tr> 
            <td></td> 
            <td> <b>Payment Due Date:</b><br> {{ doc.due_date or "" }}</td> 
            {% if doc.tax_id %} 
            <td> <b>Customer Tax ID:</b> <br> {{ doc.tax_id or "" }}</td> 
            {% else %} 
            <td></td> 
            {% endif %} 
        </tr> 
        <tr> 
            {% if doc.status %} <td> <b>Status:</b> <br> {{ doc.status or "" }}</td> {% endif %} 
        </tr>
    </tbody>
</table>
   {% if doc.po_no or doc.po_date %}
    <div>
        <table style="width: 100%; background-color: white; border-collapse: collapse;">
            <tr style="width: 100%; background-color: white;">
                <th style="width: 50%; text-align: left; font-size: 12px; padding: 8px background-color: white;">
                    {% if doc.po_no %}
                        Purchase Order:&nbsp;&nbsp;{{ doc.po_no }}
                    {% endif %}
                </th>
                <th style="width: 50%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
                    {% if doc.po_date %}
                        Purchase Order Date: &nbsp;&nbsp;{{ doc.po_date }}
                    {% endif %}
                </th>
            </tr>
        </table>
    </div>
{% endif %}

    <br>
    
    <div>
        <p style="text-align: left; font-size: 14px;"><b> Items </b> <br></p>
        <br>
        {% set bouns = doc.items|selectattr('custom_bonus_qty')|list|length > 0 %}
        <table class="small-font-table">
            <tr style="background-color: #f0f0f0; text-align: left;">
                <th style = "width:7%; text-align: left;">ID</th>
                <th style="text-align: left;">Item Code</th>
                <th style="text-align: left;">Item Name</th>
                <th style="text-align: left;">Quantitiy</th>
                {% if bouns %}
                <th style="text-align: left;">Bonus</th>
                {% endif %}
                <th style="text-align: left;">Rate</th>
                <th style="text-align: left;">Amount</th>
            </tr>
            {% for item in doc.items %}
            <tr>
                <td style="text-align: left;">{{ item.idx }}</td>
                <td style="text-align: left;">{{ item.item_code }}</td>
                <td style="text-align: left;">{{ item.item_name }}</td>
                <td style="text-align: left;">{{ item.qty }}</td>
                {% if bouns %}
                <td style="text-align: left;">{{ item.custom_bonus_qty or ''}}</td>
                {% endif %}
                <td style="text-align: left;">{{ item.rate }} JD</td>
                <td style="text-align: left;">{{ item.amount }} JD</td>
            </tr>
            {% endfor %}
        </table>
    </div>
    <br><br>
    <div>
  <table class="no-border-table">
    <tr>
   {% set bonus_qty = doc.custom_total_qty_with_bonus if doc.custom_total_qty_with_bonus != 0 else doc.total_qty %}
    <td style="width: 60%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
      <b>Total Quantity</b> &nbsp; &nbsp; &nbsp; 
      {{ bonus_qty }}
</td>

      <td style="width: 40%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
        <b>Total</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.total }} &nbsp; JD
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
        <b>Total Taxes and Charges:</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.total_taxes_and_charges }}&nbsp;JD
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
        <b>Grand Total</b>  &nbsp; &nbsp; &nbsp; 
        {{ doc.grand_total }} &nbsp;JD
      </td>
    </tr>
    <tr>
      <td style="width: 60%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
      </td>
      <td style="width: 40%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
    <b>In Words</b>  &nbsp; &nbsp; &nbsp; 
    {{ doc.in_words }}
</td>
    </tr>
  </table>
</div>
<br><br>
{% if doc.customer_address or doc.address_display or doc.contact_display or doc.contact_mobile %}
    <div style="width: 100%;">
        <p style="text-align: left; font-size: 14px;"><b>Billing Address</b> <br></p>
        <table style="width: 100%;">
            <tr>
                {% if doc.customer_address %}
                <td style="width: 50%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
                    <b>Customer Address</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.customer_address or ''}} 
                </td>
                {% endif %}
                {% if doc.address_display %}
                <td style="width: 50%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
                    <b>Address</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.address_display or '' }} 
                {% endif %}
                </td>
            </tr>
            <tr>
                {% if doc.contact_display %}
                <td style="width: 50%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
                    <b>Contact</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.contact_display  or '' }} 
                
                </td>
                {% endif %}
                {% if doc.contact_mobile %}
                <td style="width: 50%; text-align: left; font-size: 12px; padding: 8px; background-color: white;">
                    <b>Mobile No</b> &nbsp;&nbsp;&nbsp;
                    {{ doc.contact_mobile  or ''}} 
                </td>
                {% endif %}
            </tr>
        </table>
    </div>
{% endif %}


<div>
    <p>
        {{ doc.terms }}
    </p>
</div>





<!-- page footer -->
<table id="footer" width="100%">
  <tr>
    <td width="100%">
      {{ footer }}
    </td>
  </tr>
</table>
